import React,{Component} from 'react';
import {useState} from 'react';
import {w3cwebsocket as W3CWebSocket} from 'websocket';

const webSocketServer = require('websocket').server;
const http = require('http');



const axios = require("axios")

function Home(){
    

      const [tag,setTag] = useState("")

        const search = async () => {
          let query = tag.replace(/\s+/g, '-');
          setTag("")

          axios.get("http://localhost:8080/search/"+`${query}`)
          

          const server = http.createServer();
          server.listen(8081);
          const wsServer = new webSocketServer({
            httpServer: server
          });

          wsServer.on('request', function (request) {
          
            const connection = request.accept(null, request.origin);          
            connection.on('message', function(message) {
                console.log('Received Message: ',JSON.parse(message.toString()))
              
            })
          });



          // const client = new W3CWebSocket('ws://localhost:8081/');

          //  client.onopen =  () =>{
          //   console.log("client connected")

          // }
          // client.onmessage = async (msg) =>{
          //   console.log(console.log(JSON.parse(msg.toString())))
            
          // }

        }

        return(
            <div className="container mt-3">
            <div className="row">
              <div className="col">
              </div>
              <div className="col-8">
                  <div className="input-group mb-3">
                  <input type="text" value={tag} onChange={(e)=>setTag(e.target.value)} className="form-control" placeholder="Tag"/>
                  <button className="btn btn-outline-secondary" onClick={search} type="button" id="button-addon2">Search</button>
                  </div>
              </div>
              <div className="col">
              </div>
            </div>
          </div>
        )
    
}
export default Home;
