import React,{Component} from 'react';
import {useState} from 'react';

const app = require("express");
const http = require("http");
const io = require("socket.io");

const WebSocket = require("ws");


const axios = require("axios")

function Home(){
    

      const [tag,setTag] = useState("")

        const search = async () => {
          let query = tag.replace(/\s+/g, '-');
          setTag("")

          axios.get("http://localhost:8080/search/"+`${query}`)
          

          const wsServer = new WebSocket.Server({ port: 8081 });

          wsServer.on('connection', ws => {
            console.log("client connected")
            ws.on('message',msg => {
                console.log(JSON.parse(msg.toString()))
            })
          })          

        }

        return(
            <div className="container mt-3">
            <div className="row">
              <div className="col">
              </div>
              <div className="col-8">
                  <div className="input-group mb-3">
                  <input type="text" value={tag} onChange={(e)=>setTag(e.target.value)} className="form-control" placeholder="Tag"/>
                  <button className="btn btn-outline-secondary" onClick={search} type="button" id="button-addon2">Search</button>
                  </div>
              </div>
              <div className="col">
              </div>
            </div>
          </div>
        )
    
}
export default Home;
