import React,{Component} from 'react';
import {useState} from 'react';


const axios = require("axios")

function Home(){
    

      const [tag,setTag] = useState("")
      const [data,setData] = useState([])
      const rows = [
        {
          Creator : "",
          Tilte : "",
          Subtitle: "",
          Claps: "",
          Comments: ""
        },
      ]

        const search = async () => {
          let query = tag.replace(/\s+/g, '-');
          setTag("")
          axios.get("http://localhost:8080/search/"+`${query}`)


          const socket = new WebSocket("ws://localhost:8080/socket");
          
          socket.addEventListener('open', () => {
            console.log("client")
          })
          await socket.addEventListener('message', (msg) => {
            var joined = data.push(msg.data);
            setData(joined)
          })
          console.log(data)
        }

        return(
            <div className="container mt-3">
            <div className="row">
              <div className="col">
              </div>
              <div className="col-8">
                  <div className="input-group mb-3">
                  <input type="text" value={tag} onChange={(e)=>setTag(e.target.value)} className="form-control" placeholder="Tag"/>
                  <button className="btn btn-outline-secondary" onClick={search} type="button" id="button-addon2">Search</button>
                  </div>
              </div>
              <div className="col">
              </div>

              <table className="table">
              <thead className="thead-dark">
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Creator</th>
                  <th scope="col">Tilte</th>
                  <th scope="col">Subtitle</th>
                  <th scope="col">Claps</th>
                  <th scope="col">Comments</th>
                </tr>
              </thead>
              <tbody>
                  
                           {/* <td>{item.title}</td> 
                          <td>{item.subtitle}</td>
                          <td>{item.claps}</td>
                          <td>{item.comments}</td>  */}
                  <tr>
                    {Array(5).map(column => <th>{column.data}</th>)}
                  </tr>
                  
              </tbody>
            </table>

            </div>
          </div>
        )
    
}
export default Home;
